#pragma once

namespace oqpi {
    
    //----------------------------------------------------------------------------------------------
    using task_uid = uint64_t;
    enum { invalid_task_uid = task_uid(0) };
    //----------------------------------------------------------------------------------------------


    //----------------------------------------------------------------------------------------------
    enum class task_priority : uint8_t
    {
        high = 0,
        above_normal,
        normal,
        below_normal,
        low,
        count,

        inherit
    };
    //----------------------------------------------------------------------------------------------

    //----------------------------------------------------------------------------------------------
    enum class task_type
    {
        fire_and_forget,
        waitable
    };
    //----------------------------------------------------------------------------------------------

} /*oqpi*/
